.module-navbar {
  padding: 0;
}

.header {
  display: flex;
  align-items: center;
  width: 100%;

  @include media-breakpoint-up(md) {
    width: auto;
  }
}

.logo-image {
  object-fit: contain;
  height: 100%;
  max-height: 26px;

  @include media-breakpoint-up(md) {
    max-height: 45px;
  }

  @include media-breakpoint-up(lg) {
    max-height: 44px;
  }
}

.navbar {
  background-color: $gray-100;
  @extend .navbar-light;

  @include media-breakpoint-up(md) {
    height: 70px;
    padding-left: 0;
    padding-right: 0;
  }

  &-brand {
    padding: 0;
  }

  &-nav {
    padding-left: 0;
    padding-right: 0;
    padding-top: 1rem;
    width: 100%;

    @include media-breakpoint-up(md) {
      align-items: center;
      padding-top: 0;
    }
  }

  &-toggler {
    border: 0;
    padding: 0;
    margin-left: auto;
  }
}

.links-div {
  display: flex;
  flex-direction: column;
  order: 2;
  padding-left: 0;
  padding-right: 0;

  @include media-breakpoint-up(md) {
    flex-direction: row;
    order: 0;

    .nav-item:first-child() {
      padding-left: 0;
    }

    .nav-item {
      align-self: center;
    }
  }
}

.answers-div {
  padding-left: 0;
  padding-right: 0;
  padding-bottom: .5rem;
  margin-left: auto;

  @include media-breakpoint-up(md) {
    padding-bottom: 0;
  }
}

.yxt-SearchBar-form {
  height: 44px;
}

.yxt-SearchBar {
  height: 44px;

  &-input {
    padding-right: var(--yxt-base-spacing)/2;
  }
}

